package EjercicosVarios;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=6;
		int b=20;
		int suma=a+b;
		int resta=a-b;
		int multi=a*b;
		int division=a%b;
		
		System.out.println("El total de la suma es:" +suma);
		System.out.println("El total de la resta es:" +resta);
		System.out.println("El total de la multiplicació es:" +multi);
		System.out.println("El total de la división es:" +division);

	}

}
